mapper.update:
UPDATE CMM_CODE
SET use_yn = #{use_yn}
WHERE grp_cd = #{grp_cd}
AND   cd = #{cd}

UPDATE CMM_GRP_CODE
SET use_yn = #{use_yn}
,   sys_type = #{sys_type}
WHERE grp_cd = #{grp_cd}

UPDATE CMM_CODE_RESOURCE
SET name = #{name}
	,description = #{description}
WHERE ref_id = #{ref_id}
AND   table_id = #{table_id}
AND locale = 'ko'
